import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { withAuth0 } from "@auth0/auth0-react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import axios from "axios";

class MyFav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      flowerArr: [],
      showFlag: false,
      instructions: "",
      photo: "",
      name: "",
      flowerId: "",
    };
  }

  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;
    axios
      .get(`https://testpractice01.herokuapp.com/getFlower?email=${email}`)
      .then((result) => {
        this.setState({
          flowerArr: result.data.flowerslist,
        });
      })
      .catch((err) => {
        console.log("error");
      });
  };

  deleteBook = (id) => {
    const { user } = this.props.auth0;
    const email = user.email;
    axios
      .delete(
        `https://testpractice01.herokuapp.com/deleteFlower/${id}?email=${email}`
      )
      .then((result) => {
        this.setState({
          flowerArr: result.data,
        });
      })
      .catch((err) => {
        console.log("Error on deleting");
      });
  };
  handleClose = () => {
    this.setState({
      showFlag: false,
    });
  };
  showUpdateFormModal = (item) => {
    this.setState({
      showFlag: true,
      instructions: item.instructions,
      photo: item.photo,
      name: item.name,
      flowerId: item._id,
    });
  };

  updateBook = (event) => {
    event.preventDefault();
    const { user } = this.props.auth0;
    const email = user.email;
    const obj = {
      instructions: event.target.instructions.value,
      photo: event.target.photo.value,
      name: event.target.name.value,
      email: email,
    };
    axios
      .put(
        `https://testpractice01.herokuapp.com/updateFlower/${this.state.flowerId}`,
        obj
      )
      .then((result) => {
        this.setState({
          booksArr: result.data,
          showFlag: false,
        });
      })
      .catch((err) => {
        console.log("Error in updating");
      });
  };

  render() {
    return (
      <>
        <h1>My Favorite Flower</h1>
        <p>This is a collection of my favorite Flowers</p>
        <BookFormModal getting={this.getting} />
        <Row xs={1} md={3} className="g-4">
          {this.state.booksArr.map((item) => {
            return (
              <>
                <Card>
                  <Card.Body>
                    <Card.Title>Title: {item.title}</Card.Title>
                    <Card.Text>Description: {item.description}</Card.Text>
                    <Card.Text>Status: {item.status}</Card.Text>
                    <Card.Text>Email: {item.email}</Card.Text>
                    <Button
                      variant="dark"
                      onClick={() => this.deleteBook(item._id)}
                    >
                      Delete
                    </Button>{" "}
                    <Button
                      variant="warning"
                      onClick={() => this.showUpdateFormModal(item)}
                    >
                      Update
                    </Button>{" "}
                  </Card.Body>
                </Card>
              </>
            );
          })}
        </Row>
        <UpdateFormModal
          show={this.state.showFlag}
          handleClose={this.handleClose}
          title={this.state.title}
          description={this.state.description}
          status={this.state.status}
          updateBook={this.updateBook}
        />
      </>
    );
  }
}

export default withAuth0(MyFav);
