import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./BestFlower.css";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import axios from "axios";
import { withAuth0 } from "@auth0/auth0-react";
import Button from "react-bootstrap/Button";

class MyFavoriteFlower extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      flowerArr: [],
    };
  }
  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;

    axios
      .get(`https://testpractice01.herokuapp.com/flowerAPI`)
      .then((result) => {
        this.setState({
          flowerArr: result.data.flowerslist,
        });
      })

      .catch((err) => {
        console.log("error");
      });
  };

  addToFav = (event) => {
    event.preventDefault();
    const { user } = this.props.auth0;
    const email = user.email;
    const obj = {
      instructions: event.target.instructions.value,
      photo: event.target.photo.value,
      name: event.target.name.value,
      email: email,
    };
    // console.log(obj)
    axios
      .post(`https://testpractice01.herokuapp.com/addflower`, obj)
      .then((result) => {
        this.setState({
          flowerArr: result.data.flowerslist,
        });
      })
      .catch((err) => {
        console.log("Error on adding data");
      });
  };
  render() {
    return (
      <>
        <h1>My Favorite Flower</h1>
        <p>This is a collection of my favorite Flowers</p>
        <Row xs={1} md={3} className="g-4">
          {this.state.flowerArr.map((item) => {
            return (
              <>
                <Card>
                  <Card.Body>
                    <Card.Title>instructions: {item.instructions}</Card.Title>
                    <Card.Img variant="top" src={item.photo} alt="" />
                    <Card.Text>name: {item.name}</Card.Text>
                    <Button
                      variant="dark"
                      onClick={() => this.addToFav(item._id)}
                    >
                      Add
                    </Button>{" "}
                  </Card.Body>
                </Card>
              </>
            );
          })}
        </Row>
      </>
    );
  }
}

export default withAuth0(MyFavoriteFlower);
