import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./BestFlower.css";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import axios from "axios";
import { withAuth0 } from "@auth0/auth0-react";

class MyFavoriteFlower extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      flowerArr: [],
      showFlag: false,
      instructions: "",
      photo: "",
      name: "",
      flowerId: "",
    };
  }
  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;

    axios
      .get(`https://testpractice01.herokuapp.com/flowerAPI?email=${email}`)
      .then((result) => {
        this.setState({
          flowerArr: result.data,
          showFlag: true,
        });
      })

      .catch((err) => {
        console.log("error");
      });
  };

  render() {
    return (
      <>
        <h1>My Favorite Flower</h1>
        <p>This is a collection of my favorite Flowers</p>
        <Row xs={1} md={3} className="g-4">
          {this.state.flowerArr?.map((item) => {
            return (
              <>
                <Card>
                  <Card.Body>
                    <Card.Title>instructions: {item.instructions}</Card.Title>
                    <Card.Img variant="top" src={item.photo} alt="" />
                    <Card.Text>name: {item.status}</Card.Text>
                    <Card.Text>Email: {item.email}</Card.Text>
                  </Card.Body>
                </Card>
              </>
            );
          })}
        </Row>
      </>
    );
  }
}

export default withAuth0(MyFavoriteFlower);
