import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./BestFlower.css";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import axios from "axios";

class MyFavoriteFlower extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      flowerArr: [],
      showFlag: false,
      instructions: "",
      photo: "",
      name: "",
      flowerId: "",
    };
  }

  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;
    axios
      .get(`https://testpractice01.herokuapp.com/flowerAPI?email=${email}`)
      .then((result) => {
        this.setState({
          flowerArr: result.data,
        });
      })
      .catch((err) => {
        console.log("error");
      });
  };

  render() {
    return (
      <>
        <h1>My Favorite Flower</h1>
        <p>This is a collection of my favorite Flowers</p>
        <BookFormModal getting={this.getting} />
        <Row xs={1} md={3} className="g-4">
          {this.state.booksArr.map((item) => {
            return (
              <>
                <Card>
                  <Card.Body>
                    <Card.Title>Title: {item.title}</Card.Title>
                    <Card.Text>Description: {item.description}</Card.Text>
                    <Card.Text>Status: {item.status}</Card.Text>
                    <Card.Text>Email: {item.email}</Card.Text>
                    <Button
                      variant="dark"
                      onClick={() => this.deleteBook(item._id)}
                    >
                      Delete
                    </Button>{" "}
                    <Button
                      variant="warning"
                      onClick={() => this.showUpdateFormModal(item)}
                    >
                      Update
                    </Button>{" "}
                  </Card.Body>
                </Card>
              </>
            );
          })}
        </Row>
        <UpdateFormModal
          show={this.state.showFlag}
          handleClose={this.handleClose}
          title={this.state.title}
          description={this.state.description}
          status={this.state.status}
          updateBook={this.updateBook}
        />
      </>
    );
  }
}

export default withAuth0(MyFavoriteFlower);
